mapper.select:
select no, subject, userid, hit, date_format(writedate, '%m-%d %h:%i') writedate from news
where ${searchKey} like '%${searchWord}%'
order by no desc limit ${onePageRecord} offset ${offsetPoint}

select no, subject, content, hit, userid, filename1, date_format(writedate, '%M %d %Y') writedate
from news
where no = ${param1}
